	      min_=a[j];
	      min_p=j;
	    }
	wk=a[min_p];
	a[min_p]=a[i-1];
	a[i-1]=wk;
	qsort(&a[i],n-i,sizeof(char),comp_char);
	qsort(a+r,n-r,sizeof(char),r_compare);
        return(-1);
      }

  return(0);
}


