public class Main{
        public static void main(String[] arg) throws IOException {
                BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
                String[] ia;

                while(true){
                        ia = in.readLine().split(" ");
                        H = Integer.parseInt(ia[0]);
                        W = Integer.parseInt(ia[1]);
                        if(H == 0 && W == 0){
                                break;
                        }
                        prob1 = new char[H][W];
                        prob2 = new char[W][H];
                        for(int i = 0;i < H; ++i){
                                prob1[i] = in.readLine().toCharArray();
                        }
                        n = Integer.parseInt(in.readLine());
                        cont = new char[n];
                        ia = in.readLine().split(" ");
                        on = 0;
                        for(int i = 0;i < n; ++i){
                                cont[i] = ia[i].charAt(0);
                                if(cont[i] == '-' || cont[i] == '+' || cont[i] == '*' || cont[i] == '/'){
                                        ++on;
                                }
                        }
                        Arrays.sort(cont);
                        con = new int[n][2];
                        co = new boolean[n];
                        os = new boolean[n];

                        boolean cs = true;

                        int c = 0;
                        for(int i = 0;i < H; ++i){
                                for(int j = 0;j < W; ++j){
                                        if((prob2[j][i] = prob1[i][j]) == '.'){
                                                con[c][0] = i;
                                                con[c][1] = j;
                                                ++c;
                                        }
                                }
                        }

                        if(c != n){
                                cs = false;
                        }
                        osc = 0;
                        for(int i = 0;i < n; ++i){
                                os[i] = sfos(con[i][0],con[i][1]);
                        }
                        if(cs && solv(0)){
                                System.out.println("Yes");
                        } else {
                                System.out.println("No");
                        }
                }
        }
        static int H;
        static int W;
        static char[][] prob1;
        static char[][] prob2;
        static int n;
        static char[] cont;
        static int[][] con;
        static boolean[] co;
        static boolean[] os;
        static int osc;
        static int ss;
        static int on;
        static boolean sfos(int a, int b){
                if(0 < a && a < H){
                        if(!(prob1[a-1][b] == '.' || prob1[a-1][b] == '#' ||
                            (0 <= (prob1[a-1][b] - '0') && (prob1[a-1][b] - '0') < 10))){
                                return false;
                        }
                }
                if(0 <= a && a < H-1){
                        if(!(prob1[a+1][b] == '.' || prob1[a+1][b] == '#' ||
                            (0 <= (prob1[a+1][b] - '0') && (prob1[a+1][b] - '0') < 10))){
                                return false;
                        }
                }
                if(0 < b && b < W){
                        if(!(prob1[a][b-1] == '.' || prob1[a][b-1] == '#' ||
                            (0 <= (prob1[a][b-1] - '0') && (prob1[a][b-1] - '0') < 10))){
                                return false;
                        }
                }
                if(0 <= b && b < W-1){
                        if(!(prob1[a][b+1] == '.' || prob1[a][b+1] == '#' ||
                            (0 <= (prob1[a][b+1] - '0') && (prob1[a][b+1] - '0') < 10))){
                                return false;
                        }
                }
                ++osc;
                return true;
        }
        static boolean solv(int c){
                if(c == on){
                        return solve(c);
                }
                for(int i = 0;i < n; ++i){
                        if(os[i] && !co[i]){
                                co[i] = true;
                                prob1[con[i][0]][con[i][1]] = cont[c];
                                prob2[con[i][1]][con[i][0]] = cont[c];
                                if(solv(c+1)){
                                        return true;
                                }
                                co[i] = false;
                        }
                }
                return false;
        }
        static boolean solve(int c){
                char b = 'n';
                if(c == n){
                        return ca();
                }
                for(int i = 0;i < n; ++i){
                        if(!co[i] && cont[i] != b){
                                b = cont[i];
                                co[i] = true;
                                prob1[con[i][0]][con[i][1]] = cont[c];
                                prob2[con[i][1]][con[i][0]] = cont[c];
                                if(solve(c+1)){
                                        return true;
                                }
                                co[i] = false;
                        }
                }
                return false;
        }
        static boolean ca(){
                for(int i = 0;i < H; ++i){
                        if(!cal(prob1[i])){
                                return false;
                        }
                }
                for(int i = 0;i < W; ++i){
                        if(!cal(prob2[i])){
                                return false;
                        }
                }
                return true;
        }
        static boolean cal(char[] s){
                int b=0,i,j;
                boolean flag = true;
                while(b < s.length){
                        for(i = b;i < s.length; ++i){
                                if(s[i] == '='){
                                        break;
                                }
                        }
                        for(j = b;j < s.length; ++j){
                                if(s[j] == '#'){
                                        break;
                                }
                        }
                        if(j-b > 2){
                                if(i != s.length){
                                        flag = flag && (calc(s,b,i) == calc(s,i+1,j));
                                } else {
                                        return false;
                                }
                        }
                        b = j+1;
                }
                return flag;
        }
        static int calc(char[] s, Integer t, Integer e){
                int x = 0;
                int t1 = 0;
                if(e-t==0){
                        return -100000;
                }
                for(int i = t;t < e; ++t){
                        if(0 < s[t]-'0' && s[t]-'0'<10){
                                t1 = t1*10+s[t]-'0';
                        } else if(s[t] == '+'){
                                return calc(s,i,new Integer(t)) + calc(s,++t,e);
                        } else if(s[t] == '-'){
                                return calc(s,i,new Integer(t)) - calc(s,++t,e);
                        } else if(s[t] == '*'){
                                t1 *= calc(s,++t,e);
                        } else if(s[t] == '/'){
                                try{
                                        t1 /= calc(s,++t,e);
                                } catch(ArithmeticException f){
                                        return -100000;
                                }
                                break;
                        }
                }
                return t1;
        }
}

import java.util.ArrayList;
import java.util.Arrays;
import java.util.Collections;
import java.util.List;
import java.util.Scanner;

//Indian Puzzle
public class Main{

	class E implements Comparable<E>{
		String s;
		int count;
		public E(String s) {
			this.s = s;
			count = 0;
			for(char c:s.toCharArray())if(Character.isUpperCase(c))count++;
		}
		public int compareTo(E o) {
			return count-o.count;
		}
	}

	int n;
	char[] cand;
	int[] ref;
	boolean[] u;
	List<E> exs;

	char[] s;
	int id;

	boolean sub(char[] t, int k, int ind){
		if(ind==t.length){
			String[] r = (new String(t)).split("=");
//			System.out.println("R[0]:" + r[0] + " R[1]:" + r[1]);
//			for(int i=0;i<n;i++){
//				if(ref[i]!=-1)System.out.println((char)(i+'A')+" -> " + cand[ref[i]]);
//			}
			id = 0;
			s = (r[0]+"$").toCharArray();
			double ex1 = exp();
			if(ex1==1<<28)return false;
			id = 0;
			s = (r[1]+"$").toCharArray();
			double ex2 = exp();
			if(ex2==1<<28)return false;
			if(Math.abs(ex1-ex2)<1e-6)return assign(k+1);
			return false;
		}
		if(!Character.isUpperCase(t[ind]))return sub(t,k,ind+1);
		int x = t[ind]-'A';
		if(ref[x]!=-1)return sub(t,k,ind+1);
		for(int i=0;i<n;i++){
			if(u[i])continue;
			u[i] = true;
			ref[x] = i;
			if(sub(t,k,ind+1))return true;
			u[i] = false;
			ref[x] = -1;
		}
		return false;
	}

	boolean assign(int k){
		if(k==exs.size())return true;
		return sub(exs.get(k).s.toCharArray(), k, 0);
	}

	char get(){
		if(Character.isUpperCase(s[id]))return cand[ref[s[id++]-'A']];
		else return s[id++];
	}

	double exp(){
		double res = fact();
		if(res==1<<28)return res;
		while(true){
			char c = get();
			if(c=='+'){
				double a = fact();
				if(a==1<<28)return a;
				res+=a;
			}
			else if(c=='-'){
				double a = fact();
				if(a==1<<28)return a;
				res-=a;
			}
			else break;
		}
		return res;
	}

	double fact(){
		double res = digit();
		if(res==1<<28)return res;
		while(true){
			char c = get();
			if(c=='*'){
				double a = digit();
				if(a==1<<28)return a;
				res*=a;
			}
			else if(c=='/'){
				double a = digit();
				if(a==1<<28||a==0)return a;
				res/=a;
			}
			else break;
		}
		id--;
		return res;
	}

	double digit(){
		double res = 0;
		char c = get();
		if(!Character.isDigit(c))return 1<<28;
		char next = get();
		if(c=='0'&&'1'<=next&&next<='9')return 1<<28;
		id--;
		while(Character.isDigit(c)){
			res = res*10 + (c-'0');
			c = get();
		}
		id--;
		return res;
	}

	void run(){
		Scanner sc = new Scanner(System.in);
		while(true){
			int h = sc.nextInt();
			int w = sc.nextInt();
			if((h|w)==0)break;
			char[][] m = new char[h][w];
			for(int i=0;i<h;i++)m[i]=sc.next().toCharArray();
			int k = 0;
			for(int i=0;i<h;i++)for(int j=0;j<w;j++)if(m[i][j]=='.')m[i][j]=(char)(k+++'A');
			exs = new ArrayList<E>();
			for(int i=0;i<h;i++){
				for(int j=0;j<w-1;j++){
					if(m[i][j]!='#'&&m[i][j+1]!='#'){
						StringBuilder sb = new StringBuilder();
						while(j<w&&m[i][j]!='#'){
							sb.append(m[i][j++]);
						}
						exs.add(new E(sb.toString()));
					}
				}
			}
			for(int j=0;j<w;j++){
				for(int i=0;i<h-1;i++){
					if(m[i][j]!='#'&&m[i+1][j]!='#'){
						StringBuilder sb = new StringBuilder();
						while(i<h&&m[i][j]!='#'){
							sb.append(m[i++][j]);
						}
						exs.add(new E(sb.toString()));
					}
				}
			}
			Collections.sort(exs);
			n = sc.nextInt();
			cand = new char[n];
			for(int i=0;i<n;i++)cand[i]=sc.next().charAt(0);
			ref = new int[n];
			Arrays.fill(ref, -1);
			u = new boolean[n];
			System.out.println(assign(0)?"Yes":"No");
		}
	}

	public static void main(String[] args) {
		new Main().run();
	}
}

