				if(a==1<<28)return a;
				res-=a;
			}
			else break;
		}
		return res;
	}

	double fact(){
		double res = digit();
		if(res==1<<28)return res;
		while(true){
			char c = get();
			if(c=='*'){
				double a = digit();
				if(a==1<<28)return a;
				res*=a;
			}
			else if(c=='/'){
				double a = digit();
				if(a==1<<28||a==0)return a;
				res/=a;
			}
			else break;
		}
		id--;
		return res;
	}

	double digit(){
		double res = 0;
		char c = get();
		if(!Character.isDigit(c))return 1<<28;
		char next = get();
		if(c=='0'&&'1'<=next&&next<='9')return 1<<28;
		id--;
		while(Character.isDigit(c)){
			res = res*10 + (c-'0');
			c = get();
		}
		id--;
		return res;
	}

	void run(){
		Scanner sc = new Scanner(System.in);
		while(true){
			int h = sc.nextInt();
			int w = sc.nextInt();
			if((h|w)==0)break;
			char[][] m = new char[h][w];
			for(int i=0;i<h;i++)m[i]=sc.next().toCharArray();
			int k = 0;
			for(int i=0;i<h;i++)for(int j=0;j<w;j++)if(m[i][j]=='.')m[i][j]=(char)(k+++'A');
			exs = new ArrayList<E>();
			for(int i=0;i<h;i++){
				for(int j=0;j<w-1;j++){
					if(m[i][j]!='#'&&m[i][j+1]!='#'){
						StringBuilder sb = new StringBuilder();
						while(j<w&&m[i][j]!='#'){
							sb.append(m[i][j++]);
						}
						exs.add(new E(sb.toString()));
					}
				}
			}
			for(int j=0;j<w;j++){
				for(int i=0;i<h-1;i++){
					if(m[i][j]!='#'&&m[i+1][j]!='#'){
						StringBuilder sb = new StringBuilder();
						while(i<h&&m[i][j]!='#'){
							sb.append(m[i++][j]);
						}
						exs.add(new E(sb.toString()));
					}
				}
			}
			Collections.sort(exs);
			n = sc.nextInt();
			cand = new char[n];
			for(int i=0;i<n;i++)cand[i]=sc.next().charAt(0);
			ref = new int[n];
			Arrays.fill(ref, -1);
			u = new boolean[n];
			System.out.println(assign(0)?"Yes":"No");
		}
	}

	public static void main(String[] args) {
