                                if(s[i] == '='){
                                        break;
                                }
                        }
                        for(j = b;j < s.length; ++j){
                                if(s[j] == '#'){
                                        break;
                                }
                        }
                        if(j-b > 2){
                                if(i != s.length){
                                        flag = flag && (calc(s,b,i) == calc(s,i+1,j));
                                } else {
                                        return false;
                                }
                        }
                        b = j+1;
                }
                return flag;
        }
        static int calc(char[] s, Integer t, Integer e){
                int x = 0;
                int t1 = 0;
                if(e-t==0){
                        return Integer.MIN_VALUE;
                }
                for(int i = t;t < e; ++t){
                        if(0 < s[t]-'0' && s[t]-'0'<10){
                                t1 = t1*10+s[t]-'0';
                        } else if(s[t] == '+'){
                                return calc(s,i,new Integer(t)) + calc(s,++t,e);
                        } else if(s[t] == '-'){
                                return calc(s,i,new Integer(t)) - calc(s,++t,e);
                        } else if(s[t] == '*'){
                                t1 *= calc(s,++t,e);
                        } else if(s[t] == '/'){
                                try{
                                        t1 /= calc(s,++t,e);
                                } catch(ArithmeticException f){
                                        return Integer.MIN_VALUE;
                                }
                                break;
                        }
                }
                return t1;
        }
