using namespace std;

struct comparator {
  bool operator() (const int *lhs, const int *rhs) const{
    if (lhs[2] == rhs[2] && lhs[3] == rhs[3])
      return lhs[0] < rhs[0];
    else if (lhs[2] == rhs[2])
      return lhs[3] < rhs[3];
    else
      return lhs[2] > rhs[2];
  }
};

int main(int argc, char *argv[]) {
  int n;
  set<int*, comparator> team;
  set<int> result;
  while (true) {
    cin >> n;
    if (n == 0)
      break;
    for (int i = 0; i < n; i++) {
      int *tmp = new int[4];
      cin >> tmp[0] >> tmp[1] >> tmp[2] >> tmp[3];
      team.insert(tmp);
    }

    for (set<int*>::iterator itr = team.begin();
         itr != team.end(); itr++) {
      //cout << (*itr)[0];                                                                                                                                                
      if (result.size() < 10 && result.count((*itr)[1]) < 3) {
        cout << (*itr)[0] << endl;
        result.insert((*itr)[1]);
      } else if (result.size() < 20 && result.count((*itr)[1]) < 2) {
        cout << (*itr)[0] << endl;
        result.insert((*itr)[1]);
      } else if (result.size() < 26 && result.count((*itr)[1]) < 1) {
        cout << (*itr)[0] << endl;
        result.insert((*itr)[1]);
      }
    }
    team.clear();
    result.clear();
  }
  return 0;
}

