struct P {
	int i, u, a, p;
	P(int i = 0, int u = 0, int a = 0, int p = 0): i(i), u(u), a(a), p(p){}
	bool operator < (const P &t) const {
		if (a != t.a) a > t.a;
		if (p != t.p) p < t.p;
		return i < t.i;
	}
};

vector<int> res;
int cnt[1001];

int main()
{
	int n;
	while (scanf("%d", &n), n){
		res.clear();
		memset(cnt, 0, sizeof(cnt));
		vector<P> ps;
		for (int i = 0; i < n; i++){
			int _i, u, a, p;
			scanf("%d %d %d %d", &_i, &u, &a, &p);
			ps.push_back(P(_i, u, a, p));
		}
		sort(ps.begin(), ps.end());
		for (int i = 0; i < ps.size(); i++){
			P &p = ps[i];
			if (res.size() < 10){
				if (cnt[p.u] < 3){
					res.push_back(p.i);
					cnt[p.u]++;
				}
			}
			else if (res.size() < 20){
				if (cnt[p.u] < 2){
					res.push_back(p.i);
					cnt[p.u]++;
				}
			}
			else if (res.size() < 26){
				if (cnt[p.u] < 1){
					res.push_back(p.i);
					cnt[p.u]++;
				}
			}
		}
		sort(res.begin(), res.end());
		for (int i = 0; i < res.size(); i++){
			printf("%d\n", res[i]);
		}
	}
	return 0;
