bool cmp(pair<int, int[3]> p, pair<int, int[3]> q)
{
    return (p.second[1] > q.second[1]) || (p.second[1] == q.second[1] && p.second[2] < q.second[2])
        || (p.second[1] == q.second[1] && p.second[2] == q.second[2] && p.first < q.first);
}

int main()
{
    int n;

    while (cin >> n) {
        if (!n)
            break;

        pair<int, int[3]> team[300];
        int select[1000] = {0};

        for (int i = 0; i < n; i++) {
            cin >> team[i].first >> team[i].second[0] 
                >> team[i].second[1] >> team[i].second[2];
        }
        sort(team, team + n, cmp);

        int sl = 0;  // Number of the selected team.
        for (int i = 0; i < n; i++) {
            if ((sl < 10 && select[team[i].second[0] - 1] < 3) ||
                (sl < 20 && select[team[i].second[0] - 1] < 2) ||
                (sl < 26 && select[team[i].second[0] - 1] == 0)) {
                    sl++;
                select[team[i].second[0] - 1]++;
                cout << team[i].first << endl;
            }
        }
    }

    return 0;
}

