};

bool operator<( T const& lhs, T const& rhs )
{
    return ( lhs.a > rhs.a )
        || ( lhs.a == rhs.a && lhs.p < rhs.p )
        || ( lhs.a == rhs.a && lhs.p == rhs.p && lhs.i < rhs.i );
}
ostream& operator<<( ostream& os, T const& t )
{
    os << t.i << " " << t.u << " " << t.a << " " << t.p;
    return os;
}

