        vector<Team> teams(n);
        for (int i = 0; i < n; i++) {
            cin >> teams[i].id >> teams[i].belong >> teams[i].correct >> teams[i].penalty;
        }
        for (int i = 0; i < n - 1; i++) {
            for (int j = n - 1; j > i; j--) {
                if (teams[j - 1].correct < teams[j].correct
                    || (teams[j - 1].correct == teams[j].correct && teams[j - 1].penalty > teams[j].penalty)
                    || (teams[j - 1].correct == teams[j].correct && teams[j - 1].penalty == teams[j].penalty
                        && teams[j - 1].id > teams[j].id)) {
                    swap(teams[j - 1], teams[j]);
                }
            }
        }
        int selected = 0;
        queue<int> selected_i;
        int belongs[11] = {};
        for (int i = 0; i < n; i++) {
            if (selected < 10) {
