using namespace std;

typedef pair<int, int> P;
typedef pair<int, P> Q;
typedef pair<int, Q> R;

int main() {
	int n;
	while (cin >> n) {
		if (n == 0) {
			break;
		}
		int hyou2[1005] = {0};
		priority_queue<R> pq;
		R r;
		for (int i = 0; i < n; i++) {
			cin >> ((r.second).second).first >> ((r.second).second).second >> r.first >> (r.second).first;
			pq.push(r);
		}
		vector<int> hyou;
		while (!pq.empty()) {
			R rr = pq.top();
			pq.pop();
			priority_queue< Q, vector<Q>, greater<Q> > pqq;
			pqq.push(rr.second);
			while (!pq.empty() && (pq.top()).first == rr.first) {
				pqq.push((pq.top()).second);
				pq.pop();
			}
			while (!pqq.empty()) {
				Q q = pqq.top();
				pqq.pop();
				priority_queue< P, vector<P>, greater<P> > pqp;
				pqp.push(q.second);
				while (!pqq.empty() && (pqq.top()).first == q.first) {
					pqp.push((pqq.top()).second);
					pqq.pop();
				}
				while (!pqp.empty()) {
					P p = pqp.top();
					pqp.pop();
					if (hyou.size() < 10) {
						if (hyou2[p.second] < 3) {
							hyou.push_back(p.first);
							hyou2[p.second]++;
						}
					} else if (hyou.size() < 20) {
						if (hyou2[p.second] < 2) {
							hyou.push_back(p.first);
							hyou2[p.second]++;
						}
					} else if (hyou.size() < 26) {
						if (hyou2[p.second] < 1) {
							hyou.push_back(p.first);
							hyou2[p.second]++;
						}
					}
				}
			}
		}
		for (int i = 0; i < hyou.size(); i++) {
			cout << hyou[i] << endl;
		}
	}
	return 0;
}

