{
public:
  bool operator()(const Team& lhs, const Team& rhs)
  {
    if (lhs.A == rhs.A) {
      if (lhs.P == rhs.P)
	return lhs.I < rhs.I;
      return lhs.P < rhs.P;
    }
    return lhs.A > rhs.A;
  }
};

