} Team;

class solve
{
public:
  bool operator()(const Team& lhs, const Team& rhs)
  {
    if (lhs.A == rhs.A)
      return lhs.P < rhs.P;
    return lhs.A > rhs.A;
  }
};

int main()
{
  int n;
  while (cin >> n) {
    if (n == 0)
      break;

    vector<Team> team(n);
    for (int i = 0; i < n; ++i)
      cin >> team[i].I >> team[i].U >> team[i].A >> team[i].P;
    sort(team.begin(), team.end(), solve());

    int advance = 0;
    vector<int> univ(1000, 0);
    for (int i = 0; i < n; ++i) {
      if (advance < 10) {
	if (univ[team[i].U-1] < 3) {
	  cout << team[i].I << endl;
	  ++univ[team[i].U-1];
	}
      } else if (advance < 20) {
	if (univ[team[i].U-1] < 2) {
	  cout << team[i].I << endl;
	  ++univ[team[i].U-1];
	}
      } else if (advance < 26) {
	if (univ[team[i].U-1] < 1) {
	  cout << team[i].I << endl;
	  ++univ[team[i].U-1];
	}
      }
    }

  }
  return 0;
}

#include <iostream>
#include <algorithm>
using namespace std;

struct Team {
	int id, u, ac, time;
	bool operator<(Team &r) {
		if (ac != r.ac) return ac > r.ac;
		if (time != r.time) return time < r.time;
		return id < r.id;
	}
}teams[1000];

bool is_select(int num, int unum) {
	if (num < 10) { return unum < 3; }
	if (num < 20) { return unum < 2; }
	if (num < 26) { return unum < 1; }
	return false;
}

void solve(int n) {
	for (int i = 0; i < n; i++) {
		cin >> teams[i].id >> teams[i].u >> teams[i].ac >> teams[i].time;
	}
	sort(teams, teams + n);

	int u_num[1000] = {0};
	int num = 0;

	for (int i = 0; i < n; i++) {
		if (is_select(num, u_num[teams[i].u])) {
			cout << teams[i].id << endl;
			u_num[teams[i].u]++;
			num++;
		}
	}
}

int main() {
	int n;
	while (cin >> n, n) {
		solve(n);
	}
	return 0;
}

