#include <cstdio>
#include <queue>

using namespace std;

typedef struct Score {
	Score(int i, int u, int a, int p){
		id = i; school = u; success = a; penalty = p;
	}
	int id, school, success, penalty;
} Score;

typedef struct MyComparator {
	bool operator()(Score &a, Score &b){
		if(a.success < b.success){ return true; }
		if(a.success > b.success){ return false; }
		if(a.penalty > b.penalty){ return true; }
		if(a.penalty < b.penalty){ return false; }
		return (a.id > b.id);
	}
} MyComparator;

int main(){
	while(true){
		int n;
		scanf("%d", &n);
		if(n == 0){ break; }
		priority_queue<Score, vector<Score>, MyComparator> q;
		int cs[1001] = { 0 };
		int rank = 1;
		while(n--){
			int i, u, a, p;
			scanf("%d%d%d%d", &i, &u, &a, &p);
			q.push(Score(i, u, a, p));
		}
		while(!q.empty() && rank <= 26){
			Score s = q.top();
			if(rank <= 10){
				if(cs[s.school] < 3){ printf("%d\n", s.id); }
			}else if(rank <= 20){
				if(cs[s.school] < 2){ printf("%d\n", s.id); }
			}else{
				if(cs[s.school] < 1){ printf("%d\n", s.id); }
			}
			cs[s.school]++;
			q.pop();
		}
	}
	return 0;
}

