struct TEAM{
	int i;
	int u;
	int a;
	int p;
};
bool operator<(const TEAM &a,const TEAM &b){
	pair<pair<int,int>,int> A = make_pair(make_pair(a.a,-a.p),-a.i);
	pair<pair<int,int>,int> B = make_pair(make_pair(b.a,-b.p),-b.i);
	return A < B;
}

int main(){
	int n;
	while(cin >> n){
		vector<TEAM> team(n);
		rep(i,n)cin >> team[i].i >> team[i].u >> team[i].a >> team[i].p;
		sort(team.begin(),team.end());
		reverse(team.begin(),team.end());
		map<int,int> memo;
		vector<int> ret;

		int i=0;
		while(1){
			if(i >= team.size() || ret.size()>=26)break;
			TEAM use = team[i];
			if(ret.size()<10){
				if(memo[use.u]<3){
					ret.push_back(use.i);
					memo[use.u]++;
				}
			}else if(ret.size()<	20){
				if(memo[use.u]<2){
					ret.push_back(use.i);
					memo[use.u]++;
				}
			}else{
				if(memo[use.u]<1){
					ret.push_back(use.i);
					memo[use.u]++;
				}
			}
			i++;
		}
		rep(i,ret.size()){
			cout << ret[i] << endl;
		}
	}
}

