  bool operator < (const Team& o) const {
    if (solve != o.solve) return solve > o.solve;
    if (penalty != o.penalty) return penalty < o.penalty;
    return id < o.id;
  }
};

int main() {
  int n, i, u, a, p;
  while (cin >> n, n != 0) {
    vector<Team> v;
    for (int j = 0; j < n; j++) {
