struct U{
	int id, u, ac, p;
	U(){}
	U(int id, int u, int ac, int p): id(id), u(u), ac(ac), p(p){}
	bool operator<(const U &r) const{
		if(ac != r.ac)return ac > r.ac;
		if(p != r.p)return p < r.p;
		return id < r.id;
	}
};

