typedef struct status{
	int id;
	int unite;
	int ac;
	int pena;
}status;

int cmp(const void* a,const void* b){
	status *s1 = (status *)a;
	status *s2 = (status *)b;
	if(s1->ac > s2->ac)
		return -1;
	else if(s1->ac < s2->ac)
		return 1;
	else{
		if(s1->pena > s2->pena)
			return 1;
		else if(s1->pena < s2->pena)
			return -1;
		else{
			if(s1->id > s2->id)
				return 1;
			else
				return -1;
		}
	}
}

int main(void){
	int i, n, uc[1000]/*unitecount*/,wc;
	status s[300];
	while(1){
		scanf("%d",&n);
		if(!n) break;
		for(i = 0;i < n;i++)
			scanf("%d%d%d%d",&s[i].id,&s[i].unite,&s[i].ac,&s[i].pena);
		memset(uc,0,sizeof(uc));
		qsort(s,n,sizeof(s[0]),cmp);
		wc = 0;
		for(i = 0;i < n;i++){
			if(wc < 10 && uc[s[i].unite] < 3){
