struct Team
{
	int id, u, a, p;

	bool operator < ( const Team &b ) const
	{
		if ( a == b.a )
		{
			if ( p == b.p )
			{
				return id < b.id;
			}
			else
			{
				return p < b.p;
			}
		}
		else
		{
			return a > b.a;
		}
	}
};

int main()
{
	cin.tie( 0 );
	ios::sync_with_stdio( false );

	while ( true )
	{
		int n;
		cin >> n;

		if ( !n )
		{
			break;
		}

		vector< Team > teams( n );
		FOR( t, teams )
		{
			cin >> t.id >> t.u >> t.a >> t.p;
		}
		sort( ALL( teams ) );

		int total = 0;
		VI res;
		map<int,int> count;

		FOR( t, teams )
		{
			if ( total < 10 && count[ t.u ] < 3 ||
				 10 <= total && total < 20 && count[ t.u ] < 2 ||
				 20 <= total && total < 26 && count[ t.u ] < 1 )
			{
				res.PB( t.id );
				count[ t.u ]++;
				total++;
			}
		}

		FOR( r, res )
		{
			cout << r << endl;
		}
	}

	return 0;
}

