#include<iostream>
using namespace std;

struct Team
{
    int id, be, so, pe;
    bool selected;
};

void swap( Team& a, Team& b )
{
    Team t = a;
    a = b;
    b = t;
}

void bubbleSort( Team* t, int n, int judge )
{
    for( int i = 0; i < n; i++ )
        for( int j = n - 1; j >= i + 1; j-- )
        {
            if( judge == 0 && t[j].so > t[j-1].so )
                swap( t[j], t[j-1] );
            if( judge == 1 && t[j].so == t[j-1].so && t[j].pe < t[j-1].pe )
                swap( t[j], t[j-1] );
            if( judge == 2 && t[j].so == t[j-1].so && t[j].pe == t[j-1].pe && t[j].id < t[j-1].id )
                swap( t[j], t[j-1] );
        }
}

int main()
{
    int n, a[1001];

    while( cin >> n )
    {
        if( !n )
            break;

        for( int i = 0; i < 1001; i++ )
            a[i] = 0;

        Team* t = new Team[n];

        for( int i = 0; i < n; i++ )
        {
            t[i].selected = false;
            cin >> t[i].id >> t[i].be >> t[i].so >> t[i].pe;
        }

        bubbleSort( t, n, 0 );
        bubbleSort( t, n, 1 );
        bubbleSort( t, n, 2 );

        int cnt = 0;
        for( int i = 0; i < n; i++ )
        {
            if( cnt < 10 && a[t[i].be] < 3 )
            {
                t[i].selected = true;
                cnt++;
                a[t[i].be]++;
                continue;
            }
            if( cnt < 20 && a[t[i].be] < 2 )
            {
                t[i].selected = true;
                cnt++;
                a[t[i].be]++;
                continue;
            }
            if( cnt < 26 && a[t[i].be] == 0 )
            {
                t[i].selected = true;
                cnt++;
                a[t[i].be]++;
            }
        }

        for( int i = 0; i < n; i++ )
            if( t[i].selected )
                cout << t[i].id << endl;

        delete t;
    }

    return 0;
}

#include <iostream>
#include <map>
#include <set>
#include <queue>

#define rep(i,s,t) for(int i=s;i<t;i++)
#define mp(a,b) make_pair(a,b)

using namespace std;

int main(void){
	int n;
	int id,u,a,p;

	//降順でkeyにスコア，Valueに 昇順でkeyにペナルティ valueに 昇順でKeyにid番号 valueに所属を保持する3重map
	//これを用いてスコアをソートする
	map<int,map<int,map<int,int> > ,greater<int> > ranking;
	//それぞれのチームの数を保持する配列
	int team[1000];
	//選ばれたチームのチームIDを保持するキュー
	queue<int> select;

	while(cin >> n){
		//スコアリストをクリアする
		ranking.clear();
		if(n==0) break;

		//それぞのチームのスコアを3重mapに挿入していく
		rep(i,0,n){
			cin >> id >> u >> a >> p ;
			//登場する大学のidのみを初期化する
			team[u]=0;
			ranking[a][p][id]=u;
		}
		//スコア順に並べた3重mapを上位から順に処理する
		for(map<int,map<int,map<int,int> >,greater<int> >::iterator it=ranking.begin();it!=ranking.end();it++){
			for(map<int,map<int,int> >::iterator jt=(*it).second.begin();jt!=(*it).second.end();jt++){
				for(map<int,int>::iterator kt=(*jt).second.begin();kt!=(*jt).second.end();kt++){
					int tid=(*kt).first;
					int tsc=(*kt).second;
					//tema数が10未満の場合
					if(select.size()<10){
						if(team[tsc]<3){
							select.push(tid);
							team[tsc]++;
						}
					//tema数が20未満の場合
					}else if(select.size()<20){
						if(team[tsc]<2){
							select.push(tid);
							team[tsc]++;
						}
					//tema数が26未満の場合
					}else if(select.size()<26){
						if(team[tsc]<1){
							select.push(tid);
							team[tsc]++;
						}
					//tema数が26を超えた場合はカウントを打ち切る
					}else{
						kt=(*jt).second.end();
						jt=(*it).second.end();
						it=ranking.end();
					}
				}
			}

		}
		//キューを先頭から出力していく
		while(!select.empty()){
			cout << select.front() << endl;
			select.pop();
		}

	}

	return 0;
}

