class Rank {
public:
  int I, U, A, P;
  Rank();
  Rank(int I, int U, int A, int P) : I(I), U(U), A(A), P(P) {}

};

bool comp(const Rank &r1, const Rank &r2) {
  if(r1.A != r2.A) return r1.A > r2.A;
  if(r1.P != r2.P) return r1.P < r2.P;
  return r1.I < r2.I;
}

