typedef long double R;
R EPS = 1e-9;
inline int sig(const R &x){return abs(x) < EPS ? 0 : x > 0 ? 1 : -1;}
typedef complex<R> P;
inline  R inp(const P &a, const P &b){ return (conj(a)*b).X;}
inline R outp(const P &a, const P &b){ return (conj(a)*b).Y;}
inline int ccw(const P &s, const P &t, const P &p){
	int res = sig(outp(t-s, p-s));
	if(res) return res;
	if(sig(inp(t-s, p-s)) < 0) return -2;
	if(sig(inp(s-t, p-t)) < 0) return 2;
	return 0;
}
int intersect(const P &s0, const P &s1, const P &t0, const P &t1){
	int p = ccw(t0, t1, s0) * ccw(t0, t1, s1);
	int q = ccw(s0, s1, t0) * ccw(s0, s1, t1);
	return !(p>0 || q>0);
}

struct Data
{
	int id, af, sol, pen;
	Data( int id = 0, int af = 0, int sol = 0, int pen = 0 )
		:id(id), af(af), sol(sol), pen(pen)
	{
	}

	friend bool operator < ( const Data& d1, const Data& d2 )
	{
		return (d1.sol == d2.sol ? (d1.pen == d2.pen ? d1.id < d2.id : d1.pen < d2.pen) : d1.sol > d2.sol );
	}
};

int main(){
