struct data{
    int i, u, a, p;
    data(int _i, int _u, int _a, int _p) : i(_i), u(_u), a(_a), p(_p) {}
    bool operator<(const data other) const{
        if(a != other.a)      return a > other.a;
        else if(p != other.p) return p < other.p;
        else                  return i < other.i;               
    }
};

