		    count_t[t[i].second.second-1]++;
		    isDecided[t[i].second.first-1] = true;
		    temp++;
		}
	    }
	}

	REP(i,n){
	    if(isDecided[t[i].second.first-1]) cout << t[i].second.first << endl;
	}
    }

    return 0;
}

