struct aa {
	string name;
	int l;
	int r;
	int u;
	int d;
	vector<shared_ptr<aa>>chs;
};

int get_num(string st, int&a) {
	int num=0;
	while (a != st.size() && isdigit(st[a])) {
		num*=10;
		num+=st[a]-'0';
		a++;
	}
	return num;
}

string get_name(string st, int &a) {
	string name="";
	while (a != st.size() && 'a' <= st[a] && st[a] <= 'z') {
		name+=st[a];
		a++;
	}
	return name;
}

string get_on_name(string st,bool flag, int &a) {
	assert(st[a]=='<');
	a++;
	if (flag) {
		assert(st[a]=='/');
		a++;
	}
	string name=get_name(st,a);
	assert(st[a]=='>');
	a++;
	return name;
}



pair<pair<int, int>, pair<int, int>>get_on_num(string st, int &a) {
	int l=get_num(st,a);
	assert(st[a]==',');
	a++;
	int u=get_num(st,a);
	assert(st[a]==',');
	a++;
	int r=get_num(st,a);
	assert(st[a]==',');
	a++;
	int d=get_num(st,a);

	return make_pair(make_pair(l,r),make_pair(u,d));
}

shared_ptr<aa>solve(string st, int &a) {

	aa now;
	string aname=get_on_name(st,false,a);
	now.name=aname;



	auto p=get_on_num(st,a);
	now.l=p.first.first;
	now.r=p.first.second;
	now.u=p.second.first;
	now.d=p.second.second;

	while (st[a + 1] != '/') {
		auto naa=solve(st,a);
		now.chs.push_back(naa);
	}
	get_on_name(st,true,a);
	return make_shared<aa>(now);
}

vector<string> split(const string &str, char delim) {
	vector<string> res;
	size_t current = 0, found;
	while ((found = str.find_first_of(delim, current)) != string::npos) {
		res.push_back(string(str, current, found - current));
		current = found + 1;
	}
	res.push_back(string(str, current, str.size() - current));
	return res;
}

struct team {
	int id;
	int uni;
	int score;
};

int main() {
	while (true) {
		int N;cin>>N;
		if(!N)break;
		vector<team>teams;
		for (int i = 0; i < N; ++i) {
			int id, uni, ac, pena; cin >> id >> uni >> ac >> pena;
			teams.push_back(team{ id,uni,ac * 100000 - pena });

		}
		sort(teams.begin(), teams.end(), [](const team&l, const team&r) {return l.score==r.score?l.id<r.id:l.score > r.score; });

		map<int,int>mp;
		vector<int>anss;
		int selected_num=0;
		for (int i = 0; i < teams.size(); ++i) {
			int same_uni=mp[teams[i].uni];

			bool ok=false;
			if (selected_num < 10) {
				if(same_uni<3)ok=true;
			}
			else if (selected_num < 20) {
				if(same_uni<2)ok=true;
			}
			else if (selected_num < 26) {
				if(same_uni<1)ok=true;
			}

			if (ok) {
				anss.push_back(teams[i].id);
				mp[teams[i].uni]++;
				selected_num++;
			}
		}
		for (auto ans : anss) {
			cout<<ans<<endl;
		}
	}

	return 0;
}

