using namespace std;

const int MAX = 301;
const int UMAX = 1001;
typedef pair<int,int> P;

struct team{
  int I,U,A,P;
  bool passed;
};

int UNUM[UMAX],pass,n;
team T[MAX];
P p[3] = {P(10,3),P(20,2),P(26,1)};

void T_sort(){

  for(int i = 0; i < n; i++)
    for(int j = i; j < n; j++)
      if(T[i].A < T[j].A || (T[i].A == T[j].A && T[i].P > T[j].P) ||
	 (T[i].A == T[j].A && T[i].P == T[j].P && T[i].I > T[j].I))
	swap(T[i],T[j]);
}

void solve(){

  for(int R = 0; R < 3; R++)
    for(int i = 0; pass < p[R].first && i < n; i++){
      if(T[i].passed == false && UNUM[T[i].U] < p[R].second){
	cout << T[i].I << endl;
	T[i].passed = true;
	UNUM[T[i].U]++;
	pass++;
      }
    }
}

void init(){
  pass = 0;
  fill(UNUM,UNUM+UMAX,0);
}
int main(){
  while(cin >> n && n){
    init();
    for(int i = 0; i < n; i++){
      cin >> T[i].I >> T[i].U >> T[i].A >> T[i].P;
      T[i].passed = false;
    }
    T_sort();
    solve();
  }

  return 0;
}

