using namespace std;

class Team {

    public:
        int id;
        int school;
        int cleared;
        int penalty;

        Team (int a, int b, int c, int d) {
            id = a;
            school = b;
            cleared = c;
            penalty = d;
        }

        friend bool operator<(const Team& lhs, const Team& rhs) {

            if (lhs.cleared != rhs.cleared) {
                return lhs.cleared < rhs.cleared;
            } else if (lhs.penalty != rhs.penalty) {
                return lhs.penalty > rhs.penalty;
            } else {
                return lhs.id > rhs.id;
            }
        }

};

int main() {

    int n;
    while(true) {
        cin >> n;
        if (n == 0) break;

        vector<Team> teams;
        for(int i = 0; i < n; i++) {
            int a, b, c, d;
            cin >> a >> b >> c >> d;
            teams.push_back(Team(a, b, c, d));
        }
        sort(teams.begin(), teams.end());
        reverse(teams.begin(), teams.end());

        vector<int> schools;
        int selected = 0;
        for(int i = 0; i < teams.size(); i++) {
            if (selected < 10 && count(schools.begin(), schools.end(), teams[i].school) < 3) {
                cout << teams[i].id << endl;
                selected += 1;
                schools.push_back(teams[i].school);
            } else if (selected < 20 && count(schools.begin(), schools.end(), teams[i].school) < 2) {
                cout << teams[i].id << endl;
                selected += 1;
                schools.push_back(teams[i].school);
            } else if (selected < 26 && find(schools.begin(), schools.end(), teams[i].school) == schools.end() ) {
                cout << teams[i].id << endl;
                selected += 1;
                schools.push_back(teams[i].school);
            }
        }
    }

    return 0;
}

