#define MAX 1000
using namespace std;

typedef pair<int,int> P;

int main() {
  int n;
  while(cin >> n && n!=0) {
    int u[MAX];
    priority_queue< P, vector<P>, greater<P> > que;
    for(int i=0; i<n; i++) {
      int id,a,q;
      cin >> id;
      cin >> u[id] >> a >> q;
      a=-a*1000000+q;
      que.push(P(a,id));
    }
    int d[MAX];
    for(int i=0; i<MAX; i++) d[i]=0;
    vector<int> ans;
    while(!que.empty()) {
      P p=que.top();
      que.pop();
      int a=p.first,ID=p.second;
      if(ans.size()<10 && d[u[ID]]<3) ans.push_back(ID);
      else if(ans.size()<20 && d[u[ID]]<2) ans.push_back(ID);
      else if(ans.size()<26 && d[u[ID]]<1) ans.push_back(ID);
      d[u[ID]]++;
      if(ans.size()>25) break;
    }
    for(int i=0; i<ans.size(); i++) cout << ans[i] << endl;
  }
  return 0;
}

