struct Team {
  int i, u, a, p;
};

// team1 < team2 ならtrueを返す関数
bool comp(Team team1, Team team2) {
  if (team1.a != team2.a) return team1.a > team2.a;
  if (team1.p != team2.p) return team1.p < team2.p;
  return team1.i < team2.i;
}

// 選抜基準を満たしているならtrue
bool can_qualify(int num_u, int num_qualified) {
  if (num_qualified < 10) return num_u < 3;
  if (num_qualified < 20) return num_u < 2;
  if (num_qualified < 26) return num_u < 1;
  return false;
}

int main() {
  int n;
  while(cin >> n, n) {
    vector<Team> teams(n);
    rep(i, n) {
      cin >> teams[i].i >> teams[i].u >> teams[i].a >> teams[i].p;
    }
    sort(all(teams), comp);

    const int MAX_U = 1001;
    int num_qualified = 0; // 選抜された数
    vector<int> num_u_qualified(MAX_U, 0); // 所属毎の選抜された数
    rep(i, n) {
      if (can_qualify(num_u_qualified[teams[i].u], num_qualified)) {
        num_qualified++;
        num_u_qualified[teams[i].u]++;
        cout << teams[i].i << endl;
      }
    }
  }
  return 0;
}

