namespace AOJ {
	class Program {
		static void Main(string[] args) {
			int N;
			do {
				N = Utils.GetInt();
				new Calc().Solve(N);
			} while (N!=0);

		}

		public class Calc {
			public struct team {
				public int I;
				public int A;
				public int P;
				public int U;
				public team(int i,int u,int a,int p) {
					I = i;A = a;P = p;U = u;
				}
			}
			public void Solve(int N) {
				var list = new List<team>();
				for (int i = 0; i < N; i++) {
					var line = Console.ReadLine().Split().Select(_ => int.Parse(_)).ToList();
					list.Add(new team(line[0], line[1], line[2], line[3]));
				}
				list = list.OrderByDescending(_ => _.A).ThenBy(_ => _.P).ThenBy(_=>_.I).ToList();

				var already = new int[1001];

				int cnt = 0;
				var ans = new List<int>();
				foreach (var item in list) {
					if (cnt < 10) {
						if (already[item.U] < 3) {
							already[item.U]++;
							cnt++;
							ans.Add(item.I);
						}
					}else if (cnt < 20) {
						if (already[item.U] < 2) {
							already[item.U]++;
							cnt++;
							ans.Add(item.I);
						}
					}else if (cnt < 26) {
						if (already[item.U] == 0) {
							already[item.U]++;
							cnt++;
							ans.Add(item.I);
						}
					}
				}
				foreach (var item in ans) {
					Console.WriteLine(item);
				}
				return;
			}
		}
	}

	public static class Utils {
		public static int GetInt() {
			return int.Parse(Console.ReadLine());
		}
	}
}

