alias Tuple!(int, "id", int, "af", int, "ac", int, "pn") Team;

void main(){
    while(true){
        int n = readln.chomp.to!int;
        if(n == 0) break;

        Team[] teams;
		int[int] selected;
        foreach(i; 0..n){
            auto input = readln.split.map!(to!int);
            teams ~= Team(input[0], input[1], input[2], input[3]);
            selected[input[1]] = 0;
        }

        teams.sort!("a.id < b.id", SwapStrategy.stable)();
        teams.sort!("a.pn < b.pn", SwapStrategy.stable)();
        teams.sort!("a.ac > b.ac", SwapStrategy.stable)();

        foreach(team; teams){
            if((selected.length < 10 && selected[team.af] < 3) || (selected.length < 20 && selected[team.af] < 2) || (selected.length < 26 && selected[team.af] == 0)){
                team.id.writeln;
                ++selected[team.af];
            }
        }
    }
}

