def team_sort(teams, n):
    for i in range(n - 1):
        #print(i)
        for j in range(n - i - 1):
            #print(i, j, teams[j][2], teams[j+1][2])
            if (teams[j][2] < teams[j + 1][2]):
                tmp = teams[j]
                teams[j] = teams[j+1]
                teams[j+1] = tmp
            elif (teams[j][2] == teams[j + 1][2]):
                if (teams[j][3] > teams[j + 1][3]):
                    tmp = teams[j]
                    teams[j] = teams[j+1]
                    teams[j+1] = tmp
                elif (teams[j][3] == teams[j + 1][3]) and (teams[j][0] > teams[j + 1][0]):
                    tmp = teams[j]
                    teams[j] = teams[j+1]
                    teams[j+1] = tmp

