vector<int> fact(ll L){
  ll N = L;
  vector<int> res;
  for(ll i = 2; i * i <= N; i++){
    if(L % i == 0){
      int cnt = 0;
      while(L % i == 0){
        L /= i;
        cnt++;
      }
      res.push_back(cnt);
    }
    if(L == 1) break;
  }
  if(L != 1) res.push_back(1);
  return res;
}

int main(){
  ll L;
  while(cin >> L && L){
    if(L == 1){
      cout << 1 << endl;
      continue;
    }
    vector<int> v = fact(L);
    int dp[2] = {};
    dp[1] = v[0];
    for(int i = 1; i < v.size(); i++){
      dp[(i + 1) & 1] = dp[i & 1] * 2 * v[i] + v[i] + dp[i & 1];
    }
    cout << dp[v.size() & 1] + 1 << endl;
  }
  return 0;
}

