using namespace std;

void integerFactorization(int n, vector<int>& base, vector<int>& exp)
{
    base.clear();
    exp.clear();
    int a = 2;
    while(a * a <= n){
        int b = 0;
        while(n % a == 0){
            ++ b;
            n /= a;
        }
        if(b > 0){
            base.push_back(a);
            exp.push_back(b);
        }
        ++ a;
    }
    if(n > 1){
        base.push_back(n);
        exp.push_back(1);
    }
}

int solve(int n)
{
    vector<int> base, exp;
    integerFactorization(n, base, exp);
    int m = exp.size();

    int ret = 1;
    for(int i=0; i<m; ++i)
        ret *= 2 * exp[i] + 1;
    ++ ret;
    ret /= 2;
    return ret;
}

int main()
{
    vector<int> ret(1013);
    for(int i=1; i<=1012; ++i)
        ret[i]  = solve(i);

    for(;;){
        int n;
        cin >> n;
        if(n == 0 || n > 1012)
            return 0;
        cout << ret[n] << endl;
    }
}

