int prime[500000];
int have[10];

int main () {
	bool found;
	prime[0]=2;
	int pnum = 1;
	for ( int i=3; i<1000000; i+=2 ) {
		found = false;
		for ( int j=0; j< pnum; j++ ) {
			if ( i % prime[j] == 0 ) {
				found = true;
				break;
			}
		}
		if ( !found) {
			prime[pnum]=i;
			pnum++;
		}
	}


	int l,x;
	int hc;
	while (true) {
		cin >> x;

		if ( x == 0 )
			break;
		hc=0;

		for ( int i=0; i<pnum; i++ ) {
			int count = 0;
			if ( x == 0 )
				break;
			while ( x%prime[i] == 0 ) {
				x = x / prime[i];
				count++;
			}
			if ( count != 0) {
				have[hc]=count;
				hc++;
			}
		}

		l = 1;


		for ( int i=0; i<hc; i++ ) {
			l *= have[i]+1;
		}
		for ( int i=0; i<hc; i++ ) {
			l *= 2;
		}
		l /= 2;


		cout << l << endl;
	}

}

