vector<int> prm;

vector<long long> mpl(long long n){
  map<long long,int> m;
  for(int i=0;i<prm.size();i++){
    while(n%prm[i]==0){
      m[prm[i]]++;
      n/=prm[i];
    }
  }
  if(n!=1){
    m[n]++;
  }
  map<long long,int> pf;
  pf[1]=0;
  for(map<long long,int>::iterator it=m.begin();it!=m.end();it++){
    for(map<long long,int>::iterator pit=pf.begin();pit!=pf.end();pit++){
      pit->second=0;
    }
    for(map<long long,int>::iterator pit=pf.begin();pit!=pf.end();pit++){
      if(pit->second!=it->second){
	long long n=it->first*pit->first;
	pf[n]=pit->second+1;
      }
    }
  }
  vector<long long> v;
  for(map<long long,int>::iterator pit=pf.begin();pit!=pf.end();pit++){
    v.push_back(pit->first);
  }
  return v;
}

