long long llrandom(){
	return rand()*(long long )rand()*rand()*rand();
}
long long mul(long long a,long long b,long long c){
	long long ret=0,tmp=a % c;
	for (;b;b>>=1){
		if (b & 1) if ((ret+=tmp)>=c) ret-=c;
		if ((tmp<<=1) >=c) tmp-=c;
	}
	return ret;
}
long long power(long long a,long long b,long long c){
	long long res=1;
	for (;b;b>>=1){
		if (b &1) res=mul(res,a,c);
		a=mul(a,a,c);
	}
	return res;
}
bool Miller_Rabin(long long n){
	if (n<2) return false;
	if (n==2) return true;
	if (n%2==0) return false;
	long long i,j,k,m,a;
	for (m=n-1,k=0;!(m&1);m>>=1,k++);
	for (i=0;i<MAXT;i++){
		a=power(llrandom() % (n-1) +1,m,n);
		if (a==1) continue;
		for (j=0;j<k;j++){
			if (a==n-1) break;
			a=mul(a,a,n);
		}
		if (j == k) return false;
	}
	return true;
}

long long gcd (long long a,long long b){
	return b?gcd(b,a %b):a;
}
long long f(long long x,long long n){
	return (mul(x,x,n)+1) % n;
}
long long Pollard_Rho(long long n){
	if (n<=2) return 0;
	if (!(n & 1)) return 2;
	for (long long i=1;i<=MAXT;i++){
		long long x=llrandom() % n;
		long long xx=f(x,n);
		long long p=gcd((xx+n-x) % n,n);
		while (p==1){
			x=f(x,n);
			xx=f(f(xx,n),n);
			p=gcd((xx+n-x) % n,n) % n;
		}
		if (p) return p;
	}
	return 0;
}
long long prime (long long a){
	if (Miller_Rabin(a)) return a;
	return prime( Pollard_Rho(a));
}
void fenjie(long long a){
	while (a>1){
		long long t= prime(a);
		tot++;
		fac[tot]=t;
		times[tot]=0;
		while (a % t==0){
			a/=t;
			times[tot]++;
		}
	}
}
