		long long ans=1;
		ans*=(times[1]+1);
		for (int i=2;i<=tot;i++){
			ans=(ans-1)*(2*(times[i]+1)-1)+times[i]+1;
		}
		if (n==1) ans=1;
		printf("%lld\n",ans);
	}
}

