vector<int> fact_num, take;
ll dfs(int depth)
{
	if (depth == take.size())
	{
		ll res = 1;
		for (int i = 0; i < take.size(); ++i)
			if (take[i] == fact_num[i])
				res *= fact_num[i] + 1;
		return res;
	}
	else
	{
		ll res = 0;
		for (int i = 0; i <= fact_num[depth]; ++i)
		{
			take[depth] = i;
			res += dfs(depth + 1);
		}
		return res;
	}
}
