typedef long LL;
const int SIZE = 1000001;
const int ESIZE = 1001;
LL L;
bool isPrime[SIZE];
LL P[SIZE];
int PC;
int E[ESIZE]; 
int EC;
int res;

void makePrimes() {
    PC = 0;
    memset( P, false, sizeof(P) );
    for ( int i = 3; i < SIZE; i += 2 ) {
        isPrime[i] = true; 
    }
    P[PC++] = 2;
    for ( int i = 3; i < SIZE; i += 2 ) {
        if ( ! isPrime[i] ) continue;
        P[PC++] = i;
        for ( int j = i + i; j < SIZE; j += i ) {
            isPrime[j] = false;
        }
    }
}

void init() {
    memset( E, 0, sizeof(E) );
}

void calcExp( LL L ) {
    EC = 0;
    int i = 0;
    for ( ; i < PC && L > 1; ++ i ) {
        LL p = P[i];
        if ( L % p == 0 ) {
            while ( L > 1 && L % p == 0 ) {
                E[EC] ++;
                L /= p;
            }
            EC ++;
        }
    }
    if ( i == PC ) {
        E[EC++] = 1;
    }
}

void dfs( int k, int r ) {
    if ( k >= EC ) {
        res += r;
        return;
    }
    for ( int i = 0; i < E[k]; ++ i ) dfs( k+1, r );
    dfs( k+1, r*(E[k]+1) );
}

int solve() {
    calcExp(L);
    res = 0;
    dfs( 0, 1 );
    return (res+1)/2;
}

int main() {
    makePrimes();
    while ( cin >> L && L ) {
        init();
        cout << solve() << endl;
    }
    return 0;
}

