template <class T>
map<T, int> prime_factors(T n)
{
	map<T, int> res;
	for (T i = 2; i * i <= n; ++i)
	{
		if (n % i == 0)
		{
			int c = 0;
			for ( ; n % i == 0; n /= i)
				++c;
			res[i] = c;
		}
	}
	if (n > 1)
		res[n] = 1;
	return res;
}
template <class T>
vector<T> divisors(T n)
{
	vector<T> res;
	for (T i = 1; i * i <= n; ++i)
	{
		if (n % i == 0)
		{
			res.push_back(i);
			if (i != n / i)
				res.push_back(n / i);
		}
	}
	return res;
}
int main()
{
	ll L;
	while (scanf("%lld", &L), L)
	{
		map<ll, int> f = prime_factors(L);
		vector<int> fact_num, power;
		foreach (it, f)
		{
			fact_num.push_back(it->second);
			power.push_back(1);
			for (int i = 0; i < it->second; ++i)
				power.back() *= it->first;
		}

		vector<ll> div = divisors(L);
		ll res = 1;
		foreach (it, div)
		{
			ll d = *it;

			ll pairs = 1;
			for (int i = 0; i < fact_num.size(); ++i)
				if (d % power[i] == 0)
					pairs *= fact_num[i] + 1;

			res += pairs;
		}
		res /= 2;
		printf("%lld\n", res);
	}
}

