using namespace std;
static const double EPS = 1e-9;
static const double PI = 3.141592653589793238462643383279;
typedef long long ll;

vector<int> primeTable(int n){
    vector<int> ps;
    vector<int> table(n+1, 0);
    for(int i=2; i<=n; i++){
        if(!table[i]){
            ps.push_back(i);
        if(i <= sqrt(n)) table[i*i] = i;
        }
        else{
        int j = i, step = table[i];
            while(j+step<=n){
                if(!table[j+step]) {
                    table[j+step] = step;
                    break;
                }
                else {
                    int tmp = step;
                    step = table[j+step];
                    table[j+tmp] = tmp;
                    j += tmp;
                }
            }
        }
    }
    return ps;
}

int main(){
    ll n;
    vector<int> ps=primeTable(1100000);
    int s = SZ(ps);
    while(cin>>n){
        if(n==0) break;
        vector<int> es;
        for(int i=0;n!=1 && i<s;i++){
            if(n%ps[i]==0){
                int cnt=1;
                n/=ps[i];
                while(n%ps[i]==0){
                    n /= ps[i];
                    cnt++;
                }
                es.PB(cnt);
            }
        }
        if(i==s-1 && n!=1) es.PB(1);
        ll ans=1;
        for(int i=0;i<SZ(es);i++){
            ans *= (2*es[i]+1);
        }
        cout << ans/2 + 1 << endl;
    }
    return 0;
}

