int sieve(int n) { //豎ょ?1..n 逧?エ?焚1..num 邏?焚譬?ョー莉・蜿頑ャァ諡牙?謨ー
	int p = 0;
	memset(isprime, 1, sizeof(isprime));
	for (int i = 2; i <= n; i++) {
		if (isprime[i]) {
			prime[p++] = i;
		}
		for (int j = 0; j < p && i * prime[j] <= n; j++) {
			isprime[i * prime[j]] = 0;
			if (i % prime[j] == 0) {
				break;
			}
		}
	}
	isprime[0] = isprime[1] = 0;
	return p;
}
int factorize(ll x, int factor[], int cnt[]) {
	int ret = 0;
	for (int i = 0; i < num && prime[i] <= x; i++) {
		cnt[i] = 0;
		if (x % prime[i] == 0) {
			factor[ret++] = i;
			while (x % prime[i] == 0) {
				cnt[i]++;
				x /= prime[i];
			}
		}
	}
	return ret;
}

int main() {
