const ll PRIME_SIZE = 1000010;
ll psize;
bool bprime[PRIME_SIZE+1];
ll prime[PRIME_SIZE+1];
ll Eratosthenes(ll n) {
  psize = 0;
  memset(bprime, true, sizeof(bprime));
  bprime[0] = bprime[1] = false;
  for (ll i = 2; i <= n; i++) {
    if (!bprime[i]) { continue; }
    prime[psize++] = i;
    for (ll j = i + i; j <= n; j += i) {
      bprime[j] = false;
    }
  }
  return psize;
}

int main() {
  ll l;
  Eratosthenes(1000010);
  while (cin >> l, l) {
    ll ans = 0;
    REP(i, psize) {
      if (l == 1) { break; }
      ll size = 0;
      while (l % prime[i] == 0) {
        l /= prime[i];
        size++;
      }
      if (size != 0) {
        ans = ans * (1LL + 2LL * size) + size;
        //cout << ans << " " << size << endl;
      }
    }
    if (l != 1) { ans = ans * (1 + 2); }
    ans++;
    cout << ans << endl;
  }
  return 0;
}

