template<class T> T gcd( T a, T b ) {
    return !b ? a : gcd( b, a % b );
}
template<class T> T lcm( T a, T b ) {
    return a / gcd( a, b ) * b;
}


typedef long long LL;
typedef set<LL> SI;
typedef vector<LL> VL;
LL L;

SI getDivisors( LL n ) {
    SI res;
    for ( LL i = 1; i*i <= n; ++ i ) {
        if ( n % i == 0 ) {
            res.insert(i);
            if ( i != n/i ) res.insert(n/i);
        }
    }
    return res;
}

int count( SI D_ ) {
    int res = 0;
    VL D( D_.begin(), D_.end() ); 
    for ( VL::iterator it_i = D.begin(); it_i + 1 != D.end(); ++ it_i ) {
        if ( *it_i == L ) continue;
        for ( VL::iterator it_j = it_i + 1; it_j != D.end(); ++ it_j ) {
            if ( *it_j == L ) continue;
            if ( lcm( *it_i, *it_j ) == L ) res ++;
        }
    }
    return res;
}

int solve() {
    if ( L == 1 ) return 1;
    SI D = getDivisors(L);
    return D.size();
}

int main() {
    while ( cin >> L && L ) {
        cout << solve() << endl;
    }
    return 0;
}

