void factorize(vector<ll> &p,vector<ll> &cnt,ll x) {
    p.clear(); cnt.clear();
    if(x == 1) {
        p.push_back(1);
        cnt.push_back(1);
        return;
    }

    ll pp = pl[0],pi = 0;
    while(true) {
        pp = pl[pi];
        if(x <= 1) break;
        if(x % pp == 0) {
            p.push_back(pp);
            cnt.push_back(0);
            while(x%pp == 0) {
                cnt[cnt.size()-1]++;
                x /= pp;
            }
        }
        pi++;
        if(pi == pl.size()) break;
    }
    return;
}

