long long solve(long long p) {
	long long q = p, cnt = 1;
	for (long long i = 2; i*i <= q; i++) {
		long long cnt2 = 0;
		while (p%i == 0) { p /= i; cnt2++; }
		cnt *= cnt2 * 2 + 1;
	}
	if (p >= 2)cnt *= 3;
	return cnt;
}
int main() {
	while (true) {
		long long n; cin >> n; if (n == 0)break;
		cout << (solve(n) + 1) / 2 << endl;
	}
	return 0;
}

