      if (l == 1) { break; }
      ll size = 0;
      while (l % prime[i] == 0) {
        l /= prime[i];
        size++;
      }
      if (size != 0) {
        ans = ans * (1LL + 2LL * size) + size;
        //cout << ans << " " << size << endl;
      }
    }
    if (l != 1) { ans = ans * (1 + 2) + 1; }
    ans++;
    cout << ans << endl;
  }
  return 0;
}

