		{
			break;
		}
		if(L==1)
		{
			cout<<"1"<<endl;
			continue;
		}
		if(L==2)
		{
			cout<<"2"<<endl;
			continue;
		}
		long long i,j=L,k=0,p=0,add=0,s,t,l,q;
		memset(c,0,10000000);
		memset(b,0,10000000);
		for(i=2;i<j;i++)
		{
			if(L%i==0)
			{
				k++;
				c[k]=i;
				b[k]=L/i;
				j=L/i;
			}
		}
		if(c[k]!=b[k]||(c[k]==0&&b[k]==0))
		{
			add=k*2+2;
			l=2*k+1;
			q=k;
		}
		else
		{
			add=k*2+1;
			b[k]=0;
			l=2*k;
			q=k-1;
		}
		for(i=1;i<=q;i++)
		{
			c[l-i]=b[i];
		}
		for(i=(l-1);i>1;i--)
		{
			for(j=i-1;j>=1;j--)
			{
				if(c[i]%c[j]==0)
				{
					continue;
				}
				p=c[i];
				s=c[j];
				p=p%s;
				t=p>s?p:s;
				s=p+s-t;
				p=t;
				for(;s!=0&&s!=1;)
				{
				    p=p%s;
			     	t=p>s?p:s;
				    s=p+s-t;
			    	p=t;
				}
