vector<int> fact(ll L){
  vector<int> res;
  for(int i = 2; i <= 1000000; i++){
    if(L % i == 0){
      int cnt = 0;
      while(L % i == 0){
        L /= i;
        cnt++;
      }
      res.push_back(cnt);
    }
    if(L == 1) break;
  }
  if(L != 1) res.push_back(1);
  return res;
}

