vector<int> pl;
int primes[1000005] = {};
typedef long long ll;


void init() {
    primes[0] = primes[1] = 1;
    for(int i=4; i<1000005; i+=2) primes[i] = 1;
    for(int i=3; i*i<1000005; i+=2)
        if(primes[i] == 0)
            for(int j=2; i*j<1000005; ++j)
                primes[i*j] = 1;

    pl.push_back(2);
    for(int i=3; i<1000005; i+=2)
        if(primes[i] == 0)
            pl.push_back(i);
    return;
}

void factorize(vector<ll> &p,vector<ll> &cnt,ll x) {
    p.clear(); cnt.clear();

    ll pp = pl[0],pi = 0;
    while(true) {
        pp = pl[pi];
        if(x <= 1) break;
        if(x % pp == 0) {
            p.push_back(pp);
            cnt.push_back(0);
            while(x%pp == 0) {
                cnt[cnt.size()-1]++;
                x /= pp;
            }
        }
        pi++;
        if(pi == pl.size()) break;
    }
    if(x > 1) {
        p.push_back(x);
        cnt.push_back(1);
    }
    return;
}

int main() {
    pl.clear();
    init();
    ll l;
    while(cin>>l, l) {
        vector<ll> fact,cnt;
        factorize(fact,cnt,l);

        ll ans = 1;

        for(int i=0; i<cnt.size(); ++i) {
            ans  *= (cnt[i]+1)*2-1;
        }
        cout<<(ans+1)/2<<endl;
    }
}

